#!/usr/bin/env sh

# Make Company
rails g scaffold Company ticker:string name:string

# Make the Annual Income Statement, references company
rails g scaffold Annual_Income_Statement period_ending:date total_revenue:integer cost_of_revenue:integer gross_profit:integer research_and_development:integer selling_general_and_administrative:integer non_recurring:integer others:integer total_operating_expenses:integer operating_income_or_loss:integer total_other_income_and_expenses_net:integer earnings_before_interest_and_taxes:integer interest_expense:integer income_before_tax:integer income_tax_expense:integer minority_interest:integer net_income_from_continuing_ops:integer discontinued_operations:integer extraordinary_items:integer effect_of_accounting_changes:integer other_items:integer net_income:integer preferred_stock_and_other_adjustments:integer net_income_applicable_to_common_shares:integer company:references

# Make the Quarterly Income Statement, references company
rails g scaffold Quarterly_Income_Statement period_ending:date total_revenue:integer cost_of_revenue:integer gross_profit:integer research_and_development:integer selling_general_and_administrative:integer non_recurring:integer others:integer total_operating_expenses:integer operating_income_or_loss:integer total_other_income_and_expenses_net:integer earnings_before_interest_and_taxes:integer interest_expense:integer income_before_tax:integer income_tax_expense:integer minority_interest:integer net_income_from_continuing_ops:integer discontinued_operations:integer extraordinary_items:integer effect_of_accounting_changes:integer other_items:integer net_income:integer preferred_stock_and_other_adjustments:integer net_income_applicable_to_common_shares:integer company:references

# Make the Annual Balance Sheet, references company
rails g scaffold Annual_Balance_Sheet period_ending:date cash_and_cash_equivalents:integer short_term_investments:integer net_receivables:integer other_current_assets:integer total_current_assets:integer long_term_investments:integer property_plant_and_equipment:integer goodwill:integer intangible_assets:integer accumulated_amortization:integer other_assets:integer deferred_long_term_asset_charges:integer total_assets:integer accounts_payable:integer short_and_current_long_term_debt:integer other_current_liabilities:integer total_current_liabilities:integer long_term_debt:integer other_liabilities:integer deferred_long_term_liability_charges:integer minority_interest:integer negative_goodwill:integer total_liabilities:integer misc_stocks_options_warrants:integer redeemable_preferred_stock:integer preferred_stock:integer common_stock:integer retained_earnings:integer treasury_stock:integer capital_surplus:integer other_stockholder_equity:integer total_stockholder_equity:integer net_tangible_assets inventory:integer company:references

# Make the Quarterly Balance Sheet, references company
rails g scaffold Quarterly_Balance_Sheet period_ending:date cash_and_cash_equivalents:integer short_term_investments:integer net_receivables:integer other_current_assets:integer total_current_assets:integer long_term_investments:integer property_plant_and_equipment:integer goodwill:integer intangible_assets:integer accumulated_amortization:integer other_assets:integer deferred_long_term_asset_charges:integer total_assets:integer accounts_payable:integer short_and_current_long_term_debt:integer other_current_liabilities:integer total_current_liabilities:integer long_term_debt:integer other_liabilities:integer deferred_long_term_liability_charges:integer minority_interest:integer negative_goodwill:integer total_liabilities:integer misc_stocks_options_warrants:integer redeemable_preferred_stock:integer preferred_stock:integer common_stock:integer retained_earnings:integer treasury_stock:integer capital_surplus:integer other_stockholder_equity:integer total_stockholder_equity:integer net_tangible_assets inventory:integer company:references

# Make the Annual Cash Flow, references company
rails g scaffold Annual_Cash_Flow period_ending:date net_income:integer depreciation:integer adjustments_to_net_income:integer changes_in_accounts_receivables:integer changes_in_liabilities:integer changes_in_inventories:integer changes_in_other_operating_activities:integer total_cash_flow_from_operating_activities:integer capital_expenditures:integer investments:integer other_cash_flows_from_investing_activities:integer total_cash_flows_from_investing_activities:integer dividends_paid:integer sale_purchase_of_stock:integer net_borrowings:integer other_cash_flows_from_financing_activities:integer total_cash_flows_from_financing_activities:integer effect_of_exchange_rate_changes:integer change_in_cash_and_cash_equivalents:integer company:references

# Make the Quarterly Cash Flow, references company
rails g scaffold Quarterly_Cash_Flow period_ending:date net_income:integer depreciation:integer adjustments_to_net_income:integer changes_in_accounts_receivables:integer changes_in_liabilities:integer changes_in_inventories:integer changes_in_other_operating_activities:integer total_cash_flow_from_operating_activities:integer capital_expenditures:integer investments:integer other_cash_flows_from_investing_activities:integer total_cash_flows_from_investing_activities:integer dividends_paid:integer sale_purchase_of_stock:integer net_borrowings:integer other_cash_flows_from_financing_activities:integer total_cash_flows_from_financing_activities:integer effect_of_exchange_rate_changes:integer change_in_cash_and_cash_equivalents:integer company:references
